*{
	margin:0 auto;
	padding: 0;
	outline: 0;
}
.container{
	width: 100%;
}
body{
	font: 16PX 'Oswald', Helvetica, Arial, sans-serif;

}
header, footer, section, aside, article{
	display: block;
}

#main-header{
	background: #6c5353ff;
	width: 100%;
	margin: 0 auto;
	padding: 0;
}



/*menu en version mobile*/





/*imagen de portada*/
.imagen-portada img{
	width: 100%;
	height: 250px;
}

/*contenido principal*/
#main-content{
	margin-top: 70px;
	margin-bottom: 50px;
}

#main-content .ejemplo1 img{

}
/*footer*/

#CONTACTO{
	background: #c8b7b7ff;
	border: 2px solid;
	border-color: #6c5353ff;
}

#CONTACTO .redes{
	display: block;

}

#CONTACTO .redes ul{
	list-style: none;
	text-decoration: none;
	float: left;
	margin-top: 9px;
	padding-left: 4px;
}

#CONTACTO .texto{
	bottom: 0px;
	margin-top: 45px;

}

/********************
****SMALL DEVICES****
********************/

@media (min-width: 415px){

	.main-nav ul{
	margin-top: 5px;
	float:right;
	}
	.main-nav a{
	list-style: none;
	text-decoration: none;
	font: bold 20px comic-sans,arial,sans-serif;
	color: white;
	}

	.main-nav li a:hover{
		color: #b3b3b3ae;
	} 
	.main-nav .nav-pills>li>a:hover{
		background: none;
	}
}

@media (max-width: 414px){

	#mobile-main-menu a{
	display: block;
	width: 100%;
	padding: 10px 0;
	border-top: 1px solid #eee;
	color: white;
	text-align: center;
	font-weight: bold;
	}

	#mobile-main-menu li {
		width: 100%;
		margin: 0;
	}

	#mobile-main-menu a:hover {
		color: #fff;
		text-decoration: none;
		background-color: #b3b3b3ff;
	}
}

@media (max-width: 960px){
	.redes{
		margin-left: 5px;
	}
	.texto{
		text-align: center;
	}
}
